    private void adDActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_adDActionPerformed
        if (!fieldIsEmpty()){
            if (sReg.ifRepetition(sNum.getText())){
                JOptionPane.showMessageDialog(null, "Student Number is Already in Use", "Warning", 0);
            } else {
                int choice = JOptionPane.showConfirmDialog(null, "Are you sure?");
                if (choice == 0){           
                    sReg.add();
                    JOptionPane.showMessageDialog(null, "Student Added", "Successful", 1);
                    clear();
                }
            }
        }else {JOptionPane.showMessageDialog(null, "All Fields Must Be Filled", "Error", 0);}
    }//GEN-LAST:event_adDActionPerformed

    private void searchActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_searchActionPerformed
        if ("".equals(sNum.getText())){
            JOptionPane.showMessageDialog(null, "Please specify the Student Number", "Error", 0);
        } else {
            if(!sReg.search(sNum.getText())) {
                JOptionPane.showMessageDialog(null, "Student Number was not found", "Error", 0);
            }
        }
    }//GEN-LAST:event_searchActionPerformed

    private void clrActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_clrActionPerformed
        clear();
    }//GEN-LAST:event_clrActionPerformed

    private void deletEActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_deletEActionPerformed
        int choice = JOptionPane.showConfirmDialog(null, "Are you sure?");
        if (choice == 0){
            sReg.delete();
            JOptionPane.showMessageDialog(null, "Student Deleted", "Successful", 1);
            clear();
            indx.setText("");
        }
    }//GEN-LAST:event_deletEActionPerformed

    private void updatEActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_updatEActionPerformed
        int choice = JOptionPane.showConfirmDialog(null, "Are you sure?");
        if (choice == 0){
            sReg.update();
            JOptionPane.showMessageDialog(null, "Student Iformation Updated", "Successful", 1);
            clear();
            indx.setText("");
        }
    }//GEN-LAST:event_updatEActionPerformed

