    //Checks if all fields are empty
    public boolean fieldIsEmpty(){
        return ("".equals(sNum.getText()) || "".equals(sFName.getText()) || "".equals(sMName.getText())
             || "".equals(sLName.getText()) || "".equals(sCourse.getText()) || "".equals(sYear.getText()) || 
                "".equals(subjCode.getText()) || "".equals(subjDesc.getText()) || "".equals(crushName.getText()));
    }
    //Clears the fields
    public void clear(){
        sNum.setText("");
        sFName.setText("");
        sMName.setText("");
        sLName.setText("");
        sCourse.setText("");
        sYear.setText("");
        crushName.setText("");
        subjCode.setText("");
        subjDesc.setText("");
    }
